import React, { Fragment,useState,useRef,useCallback } from 'react';
import Breadcrumb from '../../layout/breadcrumb'
import {Container,Card,CardHeader,CardBody} from 'reactstrap'
import ReactCrop from 'react-image-crop';
import {ImageCropper} from '../../constant'

const Imagecropper = (props)  => {
  const [upImg, setUpImg] = useState();
  const imgRef = useRef(null);
  const [crop, setCrop] = useState({ unit: '%', width: 30, aspect: 16 / 9 });
  const [previewUrl, setPreviewUrl] = useState();

  const onSelectFile = e => {
    if (e.target.files && e.target.files.length > 0) {
      const reader = new FileReader();
      reader.addEventListener('load', () => setUpImg(reader.result));
      reader.readAsDataURL(e.target.files[0]);
    }
  };

  const onLoad = useCallback(img => {
    imgRef.current = img;
  }, []);

  const makeClientCrop = async crop => {
    if (imgRef.current && crop.width && crop.height) {
      createCropPreview(imgRef.current, crop, 'newFile.jpeg');
    }
  };

  const createCropPreview = async (image, crop, fileName) => {
    const canvas = document.createElement('canvas');
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;
    canvas.width = crop.width;
    canvas.height = crop.height;
    const ctx = canvas.getContext('2d');

    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width,
      crop.height
    );

    return new Promise((resolve, reject) => {
      canvas.toBlob(blob => {
        if (!blob) {
          reject(new Error('Canvas is empty'));
          return;
        }
        blob.name = fileName;
        window.URL.revokeObjectURL(previewUrl);
        setPreviewUrl(window.URL.createObjectURL(blob));
      }, 'image/jpeg');
    });
  };

  return (
    <Fragment>
    <Breadcrumb parent="Bouns Ui" title="Image Cropper"/>
    <Container fluid={true}>
      <Card>
          <CardHeader>
              <h5>{ImageCropper}</h5>
          </CardHeader>
          <CardBody>
              <div className="input-cropper">
                  <input type="file" onChange={onSelectFile} />
              </div>
              <ReactCrop
                src={upImg}
                crop={crop}
                onImageLoaded={onLoad}
                onChange={c => setCrop(c)}
                onComplete={makeClientCrop}
              />
              {previewUrl && <img alt="Crop preview" src={previewUrl}  style={{ maxWidth: "100%" }}  className="crop-portion"  />}
            </CardBody>
        </Card>
      </Container>
      </Fragment>

  );
}

export default Imagecropper;
